import javax.swing.JOptionPane; public class Principal{public static void main(String[] args){int[] v= {110,20,70};int res = promedio(v);;JOptionPane.showMessageDialog(null,"El promedio de los valores del arreglo es: "+res);}static int promedio(int[] v){int prom = 0;for(int i:v){prom = prom+i;}prom = prom/3;return prom;}}